import { ChevronRight } from 'lucide-react';
import { useEffect, useRef, useState } from 'react';

export default function NewsUpdatesSection() {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isPaused, setIsPaused] = useState(false);

  const intervalRef = useRef(null);

  const newsItems = [
    {
      id: 1,
      date: 'Oct 1, 2025',
      title: 'New Nutrition Guidelines Released for Mid Day Meal Program',
      description: 'Ministry of Education announces updated nutritional standards to ensure balanced meals for students.',
      category: 'Policy Update',
      link: 'https://example.com/news1'
    },
    {
      id: 2,
      date: 'Sep 28, 2025',
      title: 'Digital Payment System Upgrade Completed Successfully',
      description: 'Enhanced security features and faster processing times now available for all registered schools.',
      category: 'Technology',
      link: 'https://example.com/news2'
    },
    {
      id: 3,
      date: 'Sep 25, 2025',
      title: 'State-Level Training Program for Cook-cum-Helpers Announced',
      description: 'Comprehensive training on hygiene and food safety practices scheduled for November 2025.',
      category: 'Training',
      link: 'https://example.com/news3'
    }
  ];

  const grItems = [
    {
      id: 1,
      title: 'Revised Fund Allocation Guidelines',
      image: '/images/gr1.jpg',
      link: '/images/gr1.jpg'
    },
    {
      id: 2,
      title: 'Updated School Registration Process',
      image: '/images/gr2.jpg',
      link: '/images/gr2.jpg'
    },
    {
      id: 3,
      title: 'Menu Planning and Implementation',
      image: '/images/gr3.jpg',
      link: '/images/gr3.jpg'
    }
  ];

  const handleNextSlide = () => {
    setCurrentSlide((prev) => (prev === newsItems.length - 1 ? 0 : prev + 1));
  };

  useEffect(() => {
    if (!isPaused) {
      intervalRef.current = setInterval(handleNextSlide, 4000);
    }

    return () => clearInterval(intervalRef.current);
  }, [isPaused, currentSlide]);

  return (
    <div className="py-12 px-4 bg-gray-50">
      <div className="max-w-7xl mx-auto space-y-16">

        {/* Section: Latest News */}
        <section
          onMouseEnter={() => setIsPaused(true)}
          onMouseLeave={() => setIsPaused(false)}
          className="bg-white rounded-lg shadow-md overflow-hidden"
        >
          <div className="p-8">
            <h2 className="text-2xl font-bold text-orange-600 mb-6">📰 Latest News</h2>
            <div>
              <span className="inline-block bg-orange-100 text-orange-600 text-xs font-semibold px-3 py-1 rounded-full mb-2">
                {newsItems[currentSlide].category}
              </span>
              <p className="text-sm text-gray-500 mb-1">{newsItems[currentSlide].date}</p>
              <h3 className="text-xl font-bold text-gray-800 mb-2">{newsItems[currentSlide].title}</h3>
              <p className="text-gray-600 mb-4">{newsItems[currentSlide].description}</p>
              <a
                href={newsItems[currentSlide].link}
                target="_blank"
                rel="noopener noreferrer"
                className="text-orange-600 font-semibold hover:text-orange-700 transition"
              >
                Read More →
              </a>
            </div>
            <div className="flex justify-center gap-2 mt-6">
              {newsItems.map((_, index) => (
                <button
                  key={index}
                  onClick={() => setCurrentSlide(index)}
                  className={`w-2 h-2 rounded-full transition-all ${
                    currentSlide === index ? 'bg-orange-500 w-6' : 'bg-gray-300'
                  }`}
                />
              ))}
            </div>
          </div>
        </section>

        {/* Section: Government Resolutions */}
        <section className="bg-white rounded-lg shadow-md p-8">
          <h2 className="text-2xl font-bold text-green-600 mb-6">📄 Government Resolutions (GRs)</h2>
          <div className="grid md:grid-cols-3 gap-6">
            {grItems.map((item) => (
              <div key={item.id} className="group cursor-pointer">
                <a href={item.link} target="_blank" rel="noopener noreferrer">
                  <img
                    src={item.image}
                    alt={item.title}
                    className="rounded-lg border border-gray-200 hover:shadow-lg transition-all"
                  />
                  <p className="mt-2 text-gray-700 font-semibold group-hover:text-green-700 transition">{item.title}</p>
                </a>
              </div>
            ))}
          </div>
        </section>

        {/* Section: Information */}
        <section className="bg-white rounded-lg shadow-md p-8">
          <h2 className="text-2xl font-bold text-blue-600 mb-6">ℹ️ Mid Day Meal Scheme Information</h2>
          <div className="text-gray-700 space-y-4">
            <p>
              The <strong>Mid Day Meal Scheme</strong> is a flagship program of the Government of India aimed at improving
              the nutritional status of school-age children nationwide. The program provides free lunches to children in
              primary and upper primary classes in government and government-aided schools.
            </p>
            <p>
              It also aims to enhance school enrollment, attendance, and retention while simultaneously improving the
              nutritional levels among children.
            </p>
            <p>
              The meals served follow strict nutritional guidelines and are regularly monitored for quality and hygiene.
              The scheme has a direct impact on the physical and mental development of children and supports the overall
              goal of universal education.
            </p>
            <p>
              📌 <a href="https://pmposhan.education.gov.in/" target="_blank" rel="noopener noreferrer" className="text-blue-600 underline hover:text-blue-800">Visit Official PM Poshan Website →</a>
            </p>
            <p>
              📌 <a href="https://mdm.nic.in/mdm_website/" target="_blank" rel="noopener noreferrer" className="text-blue-600 underline hover:text-blue-800">Ministry of Education - Mid Day Meal Portal →</a>
            </p>
          </div>
        </section>

      </div>
    </div>
  );
}
